INSERT INTO `ds_article` (`id`, `fid`, `title`, `introtext`, `published`, `catid`, `user_id`, `order`, `access`, `is_comment`, `keyword`, `remark`, `addtime`) VALUES
(36, 36, '常见问题', '<div class="help_r_con">\r\n	<div>\r\n		<div>\r\n			<p>\r\n				<strong>问：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">我的订单为什么被关闭了？</span><br />\r\n<strong>答：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">在用户下达订单后，状态为“未支付”的订单，自创建时起1天即24小时后系统将自动关闭交易。需要您重新选择商品，加入购物车，再提交订单 。</span><br />\r\n<br />\r\n<strong>问：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">订单支付时提示“超出限额”,是何原因?</span><br />\r\n<strong>答：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">(1)信用卡支付限额为2000元。2000元以上的商品信用卡无法完成单笔支付。因为部分银行对支付宝信用卡支付有限额，为保证您能够正常购买，建议使用储值卡来支付，同时建议先将购机款充值到支付宝余额中（注意不是余额宝），便于您购机，谢谢！</span><br />\r\n<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">（2）网上银行提示超限，需要联系您银行卡的银行，提高您的限额。</span><br />\r\n<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">(3)因为支付宝可以设置单笔支付与日累计限额，需要更改支付限额的可以进入支付宝网页查看。</span><br />\r\n<br />\r\n<strong>问：&nbsp;</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">我的订单使用别人的支付宝支付不了？</span><br />\r\n<strong>答：&nbsp;</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">因为当您下单后并登陆你的支付宝账号支付后，系统默认将该订单与您的支付宝账号绑定，请勿更换支付宝付款，以免出现无法付款状态，建议使用同一支付宝账号支付。？</span><br />\r\n<br />\r\n<strong>问：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">安装收费吗？</span><br />\r\n<strong>答：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">您\r\n收到产品之后，如需安装请拨打海信全国统一安装专线4008-011-111预约安装。因用户自行安装产生的损失或者意外责任难以界定，海信将不予承担。\r\n目前电视产品厂家上门安装需要收取相应的服务费用，其他产品首次上门不收费，只是收取安装材料费，具体标准按照各地规定执行，详情咨询海信服务热线\r\n4008-011-111。</span><br />\r\n<br />\r\n<strong>问：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">产品退换货如何操作？</span><br />\r\n<strong>答：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">签收前申请退款，如果还未发货，我们会第一时间审核通过退款（节假日除外）；如果订单已发货或您拒收了，我们需要3-7个工作日确认电视已在返回途中，请您耐心等待，在确认电视返回后为您审核退款。请您充分考虑后再下单，以免给您带来不便。具体到退换货分两类：</span><br />\r\n<br />\r\n<strong>1、质量问题30天包退换：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">所有商品，售出后30天内出现性能问题，经当地海信售后鉴定，并出具质量鉴定报告后，请您通知在线客服，我们为您办理退换货。具体情况如下：</span><br />\r\n<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">(1)若您需要退机，售后会联系您上门拉机，机器拉走之后，您登陆支付宝账号后台（商城后台暂不支持）申请退款即可，退款客服为您处理退款事宜；</span><br />\r\n<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">（2）若您需要换机，售后会直接为您换机；可以拨打商城客服热线4006 111 111查询具体进度，若处理周期太长，您可以联系我们在线客服，客服这边给您催促跟进当地分公司办理。</span>\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', 1, 39, 1, 1, 1, 0, '常见问题', '常见问题', '1430364472'),
(41, 41, '购物流程', '<p>\r\n	<span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">注册/登陆——&gt;搜索商品——&gt;加入购物车——&gt;选择订单——&gt;填写收货人、选择付款方式及送货方式——&gt;提交订单——&gt;支付——&gt;验货签收</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">具体操作步骤如下</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:12px;font-family:''微软雅黑'',''sans-serif'';color:red;background:white;">&nbsp; &nbsp; 注册/登录</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:12px;font-family:微软雅黑, sans-serif;">1、<span style="font-size:9px;font-family:''Times New Roman'';">&nbsp; </span></span><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">进入海信商城，点击网站左上角[登陆/免费注册]按钮，进入登陆/免费注册界面。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	2<span style="font-size:12px;font-family:微软雅黑, sans-serif;">、</span>&nbsp;<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;">第一次购买用户根据提示进行注册，目前海信商城支持使用手机号注册。注册</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;">&nbsp; &nbsp; 成功后系统将自动登陆您新注册的账号。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;">&nbsp; <br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"><span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;">3<span style="font-size:12px;font-family:微软雅黑, sans-serif;">、</span>已注册用户，可以选择在登陆页面输入用户名和密码进行登陆。目前海信商城支持使用注册用</span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"><span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;">&nbsp; &nbsp; 户（手机号）登录</span></span> \r\n</p>\r\n<p>\r\n	<span style="font-family:微软雅黑, sans-serif;font-size:12px;background-color:white;">&nbsp;</span><span style="color:red;font-family:微软雅黑, sans-serif;font-size:12px;background-color:white;">搜索商品：</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">1</span><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">、您可以通过在搜索栏里输入关键字来搜索你想要购买的商品。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">2</span><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">、也可以通过分类导航栏来选择您想要购买的商品分类，根据分类找到您想要购买的商品</span></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;"></span></span> \r\n</p>\r\n<p>\r\n	<strong><span style="font-size:12px;font-family:''微软雅黑'',''sans-serif'';color:#E5004B;">加入购物车</span></strong> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;"></span></span>1<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;"></span><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">、</span></span><span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;">在商品列表或商品详情页中，点击“加入购物车”，商品会自动添加到您的购物车中</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;">2<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;"></span><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">、</span></span></span><span style="font-size:12px;font-family:微软雅黑, sans-serif;"><span style="font-size:9px;font-family:''Times New Roman'';">&nbsp;&nbsp;</span></span><span style="font-size:12px;font-family:微软雅黑, sans-serif;background:white;">在购物车中的商品系统默认订购数量为1件，如果需要更改商品数量，需在商品所在栏目后的商品数量框中修改所需购买的数量。</span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"></span> \r\n</p>\r\n<p>\r\n	<span style="font-size:9pt;font-family:微软雅黑, sans-serif;background:white;"></span> \r\n</p>', 1, 39, 1, 1, 1, 0, '购物流程', '购物流程', '1453182657'),
(42, 42, '在线充值', '<div class="help_r_con">\r\n	<div>\r\n		<div>\r\n			<p>\r\n				<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">在线支付目前只支持支付宝付款。</span><br />\r\n<strong>温馨提醒：</strong><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">目前各银行对于支付宝网上支付均有卡种、金额的限制，因各银行政策不同，建议您在申请支付宝网上支付功能时向银行咨询相关事宜。为了不影响您付款，建议可以先将钱充值到支付宝余额</span><span style="color:red;font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">（不是余额宝）</span><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">&nbsp;，支付时用支付宝余额支付即可。</span> \r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', 1, 39, 1, 1, 1, 0, '在线充值', '在线充值', '1463575041'),
(43, 43, '网银转账', '充值时可以选择网银转账模式，通过网银直接汇款到网站公司账户，填写流水号；一般审核时间为1个工作日<br />', 1, 39, 1, 1, 1, 0, '网银转账', '网银转账', '1463575192'),
(44, 44, '配送说明', '<p>\r\n	<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">配送范围说明：</span>\r\n</p>\r\n<p>\r\n	<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">&nbsp; &nbsp; &nbsp;目前海信商城电视、空调、手机等产品配送以顺丰快递为主，配送区域覆盖了全国300多个大中城市及1900多个县级市或县区，全国各省、市、直辖市及部份县城、乡镇支持送货到家；具体配送范围可登录顺丰网站</span><a href="http://www.sf-express.com/cn/sc/dynamic_functions/range/">http://www.sf-express.com/cn/sc/dynamic_functions/range/</a><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">查询或咨询在线客服</span><span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">。同时海信商城还会通过日日顺进行配送，请各位关注，有任何疑问可以咨询海信商城在线客服</span>\r\n</p>', 1, 39, 1, 1, 1, 0, '配送说明', '配送说明', '1463575272'),
(45, 45, '配送安装', '<div class="help_r_con">\r\n	<div>\r\n		<div>\r\n			<p>\r\n				<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">发\r\n货及配送时间：成功下单并付款后，3个工作日内发货，发货后2-7天可送达，送到后，请您签收前务必检查外包装箱完好，并主动要求物流人员开箱验机，如发\r\n现商品破损，请直接拒收。若机器外包装破损，请在检查机器外观的同时，通电查看屏幕状况，如物流人员不允许通电验机，则直接拒收，并备注拒签理由，如“外\r\n包破损拒签”、“通电不良”等。因未提供开箱验机服务您可拨打海信官方400-801-1111投诉。 \r\n如电视破损而您未参照验机，海信商城将不承担任何损失，还请您谅解。 建议您收到产品后，保留产品包装及说明书等附件30天。</span><br />\r\n<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">安装服务：电视产品安装需收取费用，其它产品首次上门安装免费，包装箱以外的材料费另外收费（参照海信售后收费标准）安装专线：4008-011-111</span>\r\n			</p>\r\n		</div>\r\n	</div>\r\n</div>', 1, 39, 1, 1, 1, 0, '配送安装', '配送安装', '1463575306'),
(46, 46, '温馨提示', '<p>\r\n	<span style="font-family:宋体;">亲亲，为了让您的购物旅程更开心、更完满，也为了让您尽情享受生活的美好、省去日后不必要的烦心时间，请您先用短短的</span><span style="font-family:Calibri;">5</span><span style="font-family:宋体;">分钟阅读一下我们海信电视精心总结的《购物须知》，它一定会为您省去麻烦，让您舒心购物</span><span style="font-family:Calibri;">~~</span><span style="font-family:宋体;">海信电视在此提前祝您购物愉快</span><span style="font-family:Calibri;">~~</span>\r\n</p>\r\n<p>\r\n	<span style="font-family:Calibri;">&nbsp;</span>\r\n</p>\r\n<p>\r\n	<span style="font-family:宋体;">《购物须知》</span>\r\n</p>\r\n<ol class=" list-paddingleft-2">\r\n	<li>\r\n		<p style="color:#000000;font-size:14px;font-style:normal;font-weight:normal;">\r\n			<span style="font-family:宋体;">下单及支付：</span>\r\n		</p>\r\n	</li>\r\n</ol>\r\n<p style="background:#CCE8CF;text-indent:28px;">\r\n	<span style="font-family:Calibri;">1</span><span style="font-family:宋体;">：亲亲，所有订单发货是系统自动完成，因此我们不支持下单后修改收货信息哦，请下单前务必确认收件人手机号码、姓名、地址（地址务必详细到门牌号），以免给您造成不必要的麻烦！</span>\r\n</p>\r\n<p style="background:#CCE8CF;text-indent:28px;">\r\n	<span style="font-family:Calibri;">2</span><span style="font-family:宋体;">：电视的金额相对较高，为保证您能顺利付款，请开通银行卡大额支付，大额信用卡支付限额说明请点击：</span><a href="https://cshall.alipay.com/lab/help_detail.htm?help_id=212179" target="_self"><span style="font-family:Calibri;">https://cshall.alipay.com/lab/help_detail.htm?help_id=212179</span></a><span style="font-family:宋体;">查询。或者提前将款项充值到支付宝或者余额宝哦。</span>\r\n</p>\r\n<p style="background:#CCE8CF;text-indent:28px;">\r\n	<span style="font-family:Calibri;">3</span><span style="font-family:宋体;">：本店默认开具电子发票，发票名称为：青岛增值税电子普通发票。关于发票抬头您可以在下单时，“发票抬头”栏中直接写需要开具发票抬头的名称，若无备注系统默认抬头为收货人名称。若您需要单位纸质增值税专用发票请联系客服备注。</span>\r\n</p>\r\n<ol class=" list-paddingleft-2">\r\n	<li>\r\n		<p style="color:#000000;font-size:14px;font-style:normal;font-weight:normal;">\r\n			<strong><span style="font-family:宋体;">物流配送：</span></strong>\r\n		</p>\r\n		<p style="color:#000000;font-family:;">\r\n			<span style="font-family:宋体;">送货服务：</span>\r\n		</p>\r\n		<p style="text-align:left;color:#000000;font-family:;">\r\n			<span style="font-family:宋体;">亲亲，请您在下单前在顺丰官方查询收货地址是否可以配送，如果超出送货范围请不要下单哦，目前无法保证送货服务哦，具体规则详见：</span><a href="http://www.sf-express.com/cn/sc/dynamic_functions/range/" target="_self">http://www.sf-express.com/cn/sc/dynamic_functions/range/</a>\r\n		</p>\r\n	</li>\r\n</ol>\r\n<p>\r\n	<strong><span style="font-family:宋体;">温馨提示：</span></strong>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<strong><span style="font-family:宋体;">亲亲，您下单后，请您务必保持通讯工具畅通，定期查看小能留言及好友申请，以便我们可以及时联系到您。现在物流仓库为自动流转发货，正常情况下，货物达到网店后可保存</span><span style="font-family:Calibri;">3</span></strong><strong><span style="font-family:宋体;">天，请亲尽快签收。因超出正常保存时限，且联系不到您而导致的货物返回，本店不予负责哦</span><span style="font-family:Calibri;">~</span></strong>\r\n</p>\r\n<ol class=" list-paddingleft-2">\r\n	<li>\r\n		<p style="color:#000000;font-size:14px;font-style:normal;font-weight:normal;">\r\n			<strong><span style="font-family:宋体;">商品签收：</span></strong>\r\n		</p>\r\n		<p style="color:#000000;font-family:;">\r\n			<span style="font-family:宋体;">亲亲，电视为贵重且易碎物品，我们非常不愿因验货问题给您的购买及使用过程带来不好的体验，因此亲亲一定<strong>先验货再在签收底单上确认签字</strong>，签收之前务必做到以下几点：</span>\r\n		</p>\r\n	</li>\r\n	<li>\r\n		<p style="color:#000000;font-size:14px;font-style:normal;font-weight:normal;">\r\n			<span style="font-family:宋体;">务必核实产品包装箱及机身上的型号、尺寸，确认您收到的产品与您购买的产品相符，如不相符，请您果断拒收；</span>\r\n		</p>\r\n	</li>\r\n	<li>\r\n		<p style="color:#000000;font-family:;">\r\n			<span style="font-family:宋体;">务必检查电视外包装箱是否完好：</span>\r\n		</p>\r\n	</li>\r\n	<li>\r\n		<p style="color:#000000;font-size:14px;font-style:normal;font-weight:normal;">\r\n			<span style="font-family:宋体;">若<strong>外包装箱破损或有明细撞击痕迹</strong>，请您果断拒收，并在签收单上备注</span>“<span style="font-family:宋体;">外包装破损拒签</span>”<span style="font-family:宋体;">；</span>\r\n		</p>\r\n		<p style="color:#000000;font-family:;">\r\n			<strong><span style="color:red;font-family:宋体;">例</span></strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n		</p>\r\n	</li>\r\n	<li>\r\n		<p style="color:#000000;font-family:;">\r\n			<span style="font-family:宋体;">若外包装箱完好，请您务必<strong>要求物流人员开箱验机</strong>（支持开箱验机服务，但不支持通电验机），并仔细检查电视屏幕，如屏幕破损，请您果断拒收，并在签收单上备注</span>“<span style="font-family:宋体;">电视破损拒签</span>”<span style="font-family:宋体;">；</span>\r\n		</p>\r\n	</li>\r\n	<li>\r\n		<p style="color:#000000;font-family:;">\r\n			<span style="font-family:宋体;">若物流人员不配合开箱验机，请您果断拒收，并在签收单上备注</span>“<span style="font-family:宋体;">不允许验机拒签</span>”<span style="font-family:宋体;">；</span>\r\n		</p>\r\n		<p style="color:#000000;font-family:;">\r\n			<strong><span style="font-family:宋体;">温馨提示：</span></strong>\r\n		</p>\r\n		<p style="color:#000000;font-family:;">\r\n			<strong><span style="font-family:宋体;">无论您亲自签收或他人代收，一旦在物流签收底单上确认商品完好并签字，后续出现任何三包范围外的问题，本店不予承担损失哦</span>~</strong>\r\n		</p>\r\n	</li>\r\n</ol>\r\n<p>\r\n	<strong><span style="font-family:宋体;">四、产品安装：</span></strong>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<span style="font-family:Calibri;">1</span><span style="font-family:宋体;">、安装收费及注意事项：</span>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<span style="font-family:宋体;">亲亲，海信电视<strong>安装和挂架是需要收费的</strong>，您收到商品后可以拨打海信全国统一安装专线</span><span style="font-family:Calibri;">4008011111</span><span style="font-family:宋体;">进行具体安装费用咨询并预约上门安装，如果因为您自行安装产生的损失和意外，海信将不予承担哦</span><span style="font-family:Calibri;">~</span>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<span style="font-family:Calibri;">2</span><span style="font-family:宋体;">、挂架购买途径：</span>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<span style="font-family:宋体;">海信电视标配是底座，如需购买挂架请在拨打</span><span style="font-family:Calibri;">4008011111</span><span style="font-family:宋体;">预约安装时告知，并在服务人员登门安装时直接购买</span><span style="font-family:Calibri;">~</span>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<strong><span style="font-family:宋体;">温馨提示：</span></strong>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<strong><span style="font-family:宋体;">为确保您的电视能够安全使用，非此途径购买的挂架（即质量未经专业人员认可的），海信服务人员将不予安装，敬请谅解。</span></strong>\r\n</p>\r\n<p style="text-indent:28px;">\r\n	<strong><span style="font-family:宋体;">建议亲亲签收后</span><span style="font-family:Calibri;">30</span></strong><strong><span style="font-family:宋体;">天内进行安装使用哦</span><span style="font-family:Calibri;">~</span></strong>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<strong><span style="font-family:Calibri;">&nbsp;</span></strong>\r\n</p>\r\n<ol class=" list-paddingleft-2">\r\n	<li>\r\n		<p style="color:#000000;font-size:14px;font-style:normal;font-weight:normal;">\r\n			<strong><span style="font-family:宋体;">售后服务：</span></strong>\r\n		</p>\r\n	</li>\r\n	<li>\r\n		<p style="color:#000000;font-size:14px;font-style:normal;font-weight:normal;">\r\n			<strong><span style="background:yellow;font-family:宋体;">退货及退款：</span></strong>\r\n		</p>\r\n	</li>\r\n	<li>\r\n		<p style="color:#000000;font-size:14px;font-style:normal;font-weight:normal;">\r\n			<span style="font-family:宋体;">亲亲，如您需退款，请先咨询海信商城在线客服哦</span>~\r\n		</p>\r\n	</li>\r\n	<li>\r\n		<p style="color:#000000;font-family:;">\r\n			<span style="font-family:宋体;">您在申请退款时，如包裹没有出库，我们会尽快通知物流人员取消发货并退款，如遇大型活动，退款时间可能会延长，请您理解</span>~\r\n		</p>\r\n	</li>\r\n	<li>\r\n		<p style="color:#000000;font-family:;">\r\n			<span style="font-family:宋体;">如后包裹已经出库，说明电视已经发出，请亲亲在配送人员送货上门时，<strong>务必拒收货物</strong>，由于亲亲您的原因拒收、退货，需要您承担退货产生的物流费用，收费标准约为电视总价的</span>3%<span style="font-family:宋体;">，我们会在电视返回仓库后尽快为您办理退款，请您理解</span>~\r\n		</p>\r\n	</li>\r\n</ol>\r\n<p>\r\n	<span style="font-family:Calibri;">4</span><span style="font-family:宋体;">）<a name="_GoBack"></a>如亲亲签收后<strong>不想要了</strong>，务必要<strong>保证商品未安装或使用过、附件齐全、未破坏原包装箱、不影响商家二次销售</strong>，只有这样我们才可以为您办理“</span><span style="font-family:Calibri;">7</span><span style="font-family:宋体;">天无理由退货</span><span style="font-family:Calibri;">”</span><span style="font-family:宋体;">，并安排海信售后为您上门取货，您需要承担返回运费，收费标准约为电视总价的</span><span style="font-family:Calibri;">4%</span><span style="font-family:宋体;">；如您的电视不符合以上要求，我们会拒绝您的退货申请哦</span><span style="font-family:Calibri;">~</span>\r\n</p>\r\n<p>\r\n	<span style="font-family:Calibri;">5</span><span style="font-family:宋体;">）本店活动完全按天猫要求开展，差价不补、不退。</span>\r\n</p>\r\n<p style="text-indent:28px;">\r\n	<span style="font-family:Calibri;">2</span><span style="font-family:宋体;">、</span><span style="font-family:Calibri;">30</span><span style="font-family:宋体;">天质量问题包退换：</span>\r\n</p>\r\n<p style="text-indent:28px;">\r\n	<span style="font-family:宋体;">本店产品自物流签收之日</span><span style="font-family:Calibri;">30</span><span style="font-family:宋体;">天内出现质量问题，经厂家服务人员鉴定确认，凭鉴定单，您可以免费享受退货、换货、维修服务；退货需要在电视拉回并确认入库后为您办理退款，请您理解。</span>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<strong><span style="font-family:宋体;">温馨提示：产品包装及说明书等附件请您至少保留</span><span style="font-family:Calibri;">30</span></strong><strong><span style="font-family:宋体;">天。</span></strong>\r\n</p>\r\n<p style="text-indent:0px;">\r\n	<span style="font-family:Calibri;">&nbsp;</span>\r\n</p>\r\n<ol class=" list-paddingleft-2">\r\n	<li>\r\n		<p style="color:#000000;font-size:14px;font-style:normal;font-weight:normal;">\r\n			<strong><span style="background:white;font-family:宋体;">发票：</span></strong>\r\n		</p>\r\n	</li>\r\n</ol>\r\n<p style="background:white;text-indent:28px;">\r\n	<span style="background:white;color:black;font-family:宋体;">发票金额为您的实际付款金额，即</span><strong><span style="background:white;color:red;font-family:宋体;">不含您已经享受的各项优惠金额，其中优惠金额包含但不限于红包、优惠券等</span></strong><span style="background:white;color:black;font-family:宋体;">。如您在已经开具增值税专用发票等纸质版发票后申请退款，需要您做到以下几点：</span>\r\n</p>\r\n<p style="background:white;text-indent:28px;">\r\n	<span style="background:white;color:black;"><span style="font-family:Calibri;">1</span></span><span style="background:white;color:black;font-family:宋体;">、</span><span style="background:white;color:black;font-family:;">&nbsp;&nbsp;</span><span style="background:white;color:black;font-family:宋体;">如您的发票未认证抵扣：您需要将原发票全联次（发票联和抵扣联）退回后退款；</span>\r\n</p>\r\n<p style="background:white;text-indent:28px;">\r\n	<span style="background:white;color:black;"><span style="font-family:Calibri;">2</span></span><span style="background:white;color:black;font-family:宋体;">、</span><span style="background:white;color:black;font-family:;">&nbsp;&nbsp;</span><span style="background:white;color:black;font-family:宋体;">如您的发票已认证抵扣：我们按您当地税务局要求格式给您出具退货协议，在您办理“开具红字增值税专用发票信息表”后方可退款；</span>\r\n</p>\r\n<p style="background:white;text-indent:28px;">\r\n	<span style="background:white;color:black;"><span style="font-family:Calibri;">3</span></span><span style="background:white;color:black;font-family:宋体;">、</span><span style="background:white;color:black;font-family:;">&nbsp;&nbsp;</span><span style="background:white;color:black;font-family:宋体;">如您的发票虽然未认证但已经超过认证期或无法退回：则须按扣除开票税金后的金额进行退款；</span>\r\n</p>', 1, 39, 1, 1, 1, 0, '温馨提示', '温馨提示', '1463575399'),
(47, 47, '全国联保', '<span style="font-family:微软雅黑;font-size:14px;line-height:22px;background-color:#FFFFFF;">海信商城所销售海信产品，均享受全国联保，在收到产品10-15个工作日后订单将会生成电子发票，您可以依据此电子发票享受全国联保的服务。</span>', 1, 39, 1, 1, 1, 0, '全国联保', '全国联保', '1463575426'),
(48, 48, '缤纷盛夏 商城大放价', '<p>\r\n	尊敬的海信商城会员们：\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; \r\n盛夏已至，为回馈大家各位亲们对商城一直以来的支持，海信商城5月12日至17日期间，将进行“缤纷盛夏 \r\n&nbsp;商城大放价”活动，活动期间，商城将进行赠送三重大礼包，礼包一“火爆单品买就送赠品活动”，活动期间海信手机金刚2尊享版买就送海信大礼包，加赠\r\n10000毫安移动电源；礼包二“超级返现再现商城”，二级能效920空调下单立减100，好评再返100，电视冰洗产品好评就送钱；礼包三“电视产品满\r\n3000减50，空调冰洗类产品满4000减200，满6000减400元”，精彩就戳<a href="http://newshop.hisense.com/ad/wuyue/index0511.html" target="_self">http://newshop.hisense.com/ad/wuyue/index0511.html</a>，更多活动详情欢迎大家咨询在线客服。\r\n</p>\r\n<p align="right">\r\n	海信商城\r\n</p>\r\n<p align="right">\r\n	2016年5月12日\r\n</p>', 1, 32, 1, 1, 1, 0, '缤纷盛夏 商城大放价', '缤纷盛夏 商城大放价', '1463575568');
